import React, { useEffect, useState } from 'react';

function Dashboard() {
  const [stats, setStats] = useState({ users: 0, posts: 0, categories: 0, comments: 0 });

  useEffect(() => {
    fetch('http://localhost/backend/api/dashboard_stats.php', { credentials: 'include' })
      .then(res => res.json())
      .then(data => setStats(data));
  }, []);

  return (
    <div style={{
      background: '#f8f9fa',
      padding: 20,
      borderRadius: 8,
      margin: '20px 0',
      boxShadow: '0 2px 8px #0001'
    }}>
      <h2>Dashboard</h2>
      <div style={{ display: 'flex', gap: 20 }}>
        <div><b>{stats.users}</b><br />Users</div>
        <div><b>{stats.posts}</b><br />Posts</div>
        <div><b>{stats.categories}</b><br />Categories</div>
        <div><b>{stats.comments}</b><br />Comments</div>
      </div>
    </div>
  );
}

export default Dashboard;
