import React, { useEffect, useState } from 'react';

function PostList({ userRole }) {
  const [posts, setPosts] = useState([]);
  const [editingPostId, setEditingPostId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editContent, setEditContent] = useState('');
  const [editStatus, setEditStatus] = useState('draft');

  useEffect(() => {
    fetch('http://localhost/backend/api/posts.php')
      .then(res => res.json())
      .then(data => setPosts(data));
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this post?')) return;
    await fetch('http://localhost/backend/api/delete_post.php', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify({ id }),
    });
    setPosts(posts.filter(post => post.id !== id));
  };

  const startEdit = (post) => {
    setEditingPostId(post.id);
    setEditTitle(post.title);
    setEditContent(post.content);
    setEditStatus(post.status);
  };

  const handleEdit = async (id) => {
    await fetch('http://localhost/backend/api/edit_post.php', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
      body: JSON.stringify({
        id,
        title: editTitle,
        content: editContent,
        status: editStatus,
      }),
    });
    setEditingPostId(null);
    setPosts(posts.map(post =>
      post.id === id
        ? { ...post, title: editTitle, content: editContent, status: editStatus }
        : post
    ));
  };

  return (
    <div>
      <h2>Blog Posts</h2>
      {posts.map(post => (
        <div key={post.id} style={{border: '1px solid #ccc', margin: '10px 0', padding: 20, borderRadius: 10}}>
          {editingPostId === post.id ? (
            <div>
              <input value={editTitle} onChange={e => setEditTitle(e.target.value)} />
              <textarea value={editContent} onChange={e => setEditContent(e.target.value)} />
              <select value={editStatus} onChange={e => setEditStatus(e.target.value)}>
                <option value="draft">Draft</option>
                <option value="published">Published</option>
              </select>
              <button onClick={() => handleEdit(post.id)}>Save</button>
              <button onClick={() => setEditingPostId(null)}>Cancel</button>
            </div>
          ) : (
            <>
              <h3>{post.title}</h3>
              <div>
                <small>
                  By <b>{post.author}</b> | Category: <b>{post.category || 'Uncategorized'}</b> | {new Date(post.created_at).toLocaleString()}
                </small>
              </div>
              <p>{post.content.length > 200 ? post.content.slice(0, 200) + '...' : post.content}</p>
              {(userRole === 'admin' || userRole === 'author') && (
                <>
                  <button onClick={() => startEdit(post)}>Edit</button>
                  <button onClick={() => handleDelete(post.id)} style={{color: 'red', marginLeft: 8}}>Delete</button>
                </>
              )}
            </>
          )}
        </div>
      ))}
    </div>
  );
}

export default PostList;
