import React, { useState } from 'react';
import Dashboard from './Dashboard';
import PostCreateForm from './PostCreateForm';
import CategoryCreateForm from './CategoryCreateForm';
import TagCreateForm from './TagCreateForm';

function TabbedDashboard({
  onPostCreated,
  onCategoryCreated,
  onTagCreated,
  refreshCategories,
  refreshTags
}) {
  const [activeTab, setActiveTab] = useState('dashboard');

  const tabStyle = (tab) => ({
    padding: '10px 20px',
    cursor: 'pointer',
    borderBottom: activeTab === tab ? '3px solid #2d8cff' : '3px solid transparent',
    background: activeTab === tab ? '#f4f6fb' : 'transparent',
    fontWeight: activeTab === tab ? 'bold' : 'normal',
    color: activeTab === tab ? '#2d8cff' : '#333',
    transition: 'all 0.2s'
  });

  return (
    <div>
      <div style={{
        display: 'flex',
        borderBottom: '1px solid #e0e0e0',
        marginBottom: 20,
        gap: 10
      }}>
        <div style={tabStyle('dashboard')} onClick={() => setActiveTab('dashboard')}>Dashboard</div>
        <div style={tabStyle('createpost')} onClick={() => setActiveTab('createpost')}>Create Post</div>
        <div style={tabStyle('categories')} onClick={() => setActiveTab('categories')}>Categories</div>
        <div style={tabStyle('tags')} onClick={() => setActiveTab('tags')}>Tags</div>
      </div>
      <div>
        {activeTab === 'dashboard' && <Dashboard />}
        {activeTab === 'createpost' && (
          <PostCreateForm
            onPostCreated={onPostCreated}
            refreshCategories={refreshCategories}
            refreshTags={refreshTags}
          />
        )}
        {activeTab === 'categories' && (
          <CategoryCreateForm onCategoryCreated={onCategoryCreated} />
        )}
        {activeTab === 'tags' && (
          <TagCreateForm onTagCreated={onTagCreated} />
        )}
      </div>
    </div>
  );
}

export default TabbedDashboard;
